import { Link } from "react-router-dom";

import { useLocation } from 'react-router-dom'

import parse from "html-react-parser";

function NavItem(props){

  const location = useLocation().pathname;
  const cube = document.querySelector(".cube");

  function rotateLogo(){
    if(!cube.classList.contains("rotate")){
      cube.classList.add("rotate");
      setTimeout(function(){ cube.classList.remove("rotate"); }, 1000);
    }
  }

return(
    <Link className="nav-item-link" to={props.route} onClick={this.rotateLogo}>
      <div className={location !== props.route? "nav-item": "nav-item active"}>
    {parse(props.text)}
</div>
    </Link>);
}


export default NavItem;
